function w3_open(){var a=document.getElementById("mySidebar");a.style.width="300px";a.style.paddingTop="10%";a.style.display="block"}function w3_close(){document.getElementById("mySidebar").style.display="none"}function openNav(){var a=document.getElementById("navDemo");if(a.className.indexOf("w3-show")==-1){a.className+=" w3-show"}else{a.className=a.className.replace(" w3-show","")}};